INSERT INTO `dich_vu` (`id`, `ten_dich_vu`, `mo_ta`, `hinh_anh`) VALUES
(4, 'Giúp việc nhà', 'Công việc nhà luôn là một bài toán khó đối với các bà nội trợ. Ngoài công việc ở cơ quan, họ phải lo lắng và đảm đương thêm nhiều công việc không tên khác của gia đình. Để bớt gánh nặng và có nhiều thời gian chăm sóc cho bản thân, quan tâm tới các thành viên trong gia đình, họ muốn tìm người có thể san sẻ những công việc không tên đó. Để đáp ứng nhu cầu đó, dịch vụ giúp việc nhà ra đời. Đây được xem như một sự lựa chọn sáng suốt và là “cứu cánh” cho nhiều bà nội trợ.', 'content/images_dich_vu/5fc106730b060_giadinh.png'),
(5, 'Giúp việc theo giờ', 'Không thể sắp xếp thời gian để cùng một lúc làm tất cả các công việc khiến cho nhiều người băn khoăn tìm giải pháp khắc phục. Hãy để cho những người Giúp việc làm đỡ bạn điều đó. Sử dụng người giúp việc của Giúp việc Hồng Doan, các bạn sẽ được cung cấp các loại hình dịch vụ khác nhau nhằm đáp ứng mọi nhu cầu của khách hàng. Bạn đang có nhu cầu tìm kiếm người giúp việc nhưng vẫn muốn đảm bảo không gian riêng tư của gia đình? Dịch vụ giúp việc theo giờ của Trung tâm giúp việc Hồng Nguyên hứa hẹn là lựa chọn lý tưởng dành cho bạn.', 'content/images_dich_vu/5fc106ba2b78e_icon-giupviectheogio.png'),
(6, 'Giúp việc trông trẻ', 'Hiện nay việc các gia đình tìm người giúp việc chăm sóc trẻ em, chăm bé mới sinh đang được lựa chọn ngày càng nhiều. Thuê người giúp việc trông trẻ sẽ giúp mẹ bé có sức khỏe hơn, có thời gian được nghỉ ngơi nhiều hơn. Buổi tối có thể không phải thức đêm vì trẻ quấy khóc. Sau thời gian nghỉ sinh, các bà mẹ cũng phải có người trông em bé để đi làm để không ảnh hưởng đến công việc.\r\nTuy nhiên nghề chăm sóc trẻ là một trong những nghề giúp việc khá nhạy cảm. Đòi hỏi nhiều tiêu chí ở người giúp việc, trong đó có những tiêu chí bắt buộc như sau: phải có tình thương yêu đặc biệt đối với trẻ, phải có kinh nghiệm chăm sóc trẻ em đặc biệt là trẻ sơ sinh và xử tốt các tình huống, phải có sức khỏe tốt vì trẻ em thường quấy khóc buổi tối…', 'content/images_dich_vu/5fc107e57e38a_Icon-chamsoctreem.png'),
(7, 'Giúp việc người già', 'Hiện nay, nhiều gia đình có người già, người ốm, người cao tuổi… mà con cháu lại khá bận rộn, không cân bằng được công việc ở cơ quan với công việc trong gia đình nên thời gian chăm sóc người già thật ít ỏi. Nhận thấy được nhu cầu đó, Trung tâm giúp việc Hồng Doan cung cấp dịch vụ chăm sóc người già. Trong gói dịch vụ chăm sóc người cao tuổi, giúp việc của chúng tôi được đào tạo có thể làm việc nhà, chăm sóc, trông nom cẩn thận, chu đáo cho người già cao tuổi, người ốm…', 'content/images_dich_vu/5fc1080b483ac_icon-chamsocnguoigia.png'),
(8, 'Dọn dẹp văn phòng', 'Xã hội ngày càng phát triển đi cùng với các ngành nghề đòi hỏi các kỹ năng đặc thù chuyên biệt. Nghề giúp việc tạp vụ văn phòng tại các công ty cũng là một nghề đóng một vai trò hết sức quan trọng và vô cùng nhạy cảm trong mỗi Công ty. Vậy muốn Công ty có nhiều đối tác  thu hút nhiều khách hàng thì điều tế nhị đầu tiên là Công ty phải được giữ gìn sạch sẽ. Việc này bạn hãy nhờ các nhân viên tạp vụ văn phòng giúp nhé.', 'content/images_dich_vu/5fc1083cc00cd_icon-giupviecvanphong.png'),
(9, 'Chăm sóc người bệnh', 'Cuộc sống ngày càng phát triển mọi người đều lo làm việc mà không có thời gian chăm sóc gia đình nhất là người thân của mình không may bị bệnh. Không thể sắp xếp thời gian hợp lí để vừa có thể đảm bảo công việc vừa đảm bảo chăm sóc tốt cho người thân khiến cho nhiều người băn khoăn và vô cùng áp lực. Vậy thì tại sao bạn lại không đi tìm người giúp việc thay thế mình. Hãy để cho những người làm công việc chăm sóc sức khoẻ bệnh nhân làm đỡ bạn điều đó. Đến với Trung tâm giúp việc Hồng Doan chúng tôi các bạn sẽ được cung cấp Dịch vụ chăm sóc người bệnh tại bệnh viện và tại các gia đình nhằm đáp ứng mọi nhu cầu của bạn.', 'content/images_dich_vu/5fc108643aa39_icon-chamnguoibenh.png'),
(10, 'Giúp việc ngoại quốc', 'Trong xu thế hội nhập toàn cầu, thế giới như một ngôi nhà chung. Nhu cầu  sinh sống, làm việc của những gia đình hay công ty nước ngoài tại Việt Nam ngày càng tăng cao. Nhằm đáp ứng nhu cầu về giúp việc cho người nước ngoài, Giúp việc Hồng Doan xin giới thiệu tới các bạn gói dịch vụ giúp việc cho người nước ngoài sống ở Việt Nam. Dịch vụ này giúp bạn có cuộc sống thoải mái, và có nhiều thời gian nghỉ ngơi và vui chơi tham quan, khám phá các địa điểm nổi tiếng vô cùng đẹp ở Việt Nam.', 'content/images_dich_vu/5fc108b1a2aa0_icon-giupviechonguoinuocngoai.png'),
(11, 'Giúp việc ngày tết', 'Những ngày cận Tết Nguyên Đán, ngoài việc lo lắng cho việc mua sắm đồ đạc, chuẩn bị quà Tết biếu tặng, sắp xếp hoàn thành công việc trước Tết ổn thoả thì nhiều gia đình còn vướng phải vấn đề – đôn đáo đi tìm người giúp việc trong dịp Tết đến xuân về.\r\nĐể tìm được người giúp việc vào ngày Tết không khó, nhưng tìm được giúp việc chất lượng xứng đáng với mức lương chi trả lại là một bài toán rắc rối đối với nhiều chủ nhà. Giúp việc ngày Tết sẽ làm những công việc gì? Mức lương chỉ trả như thế nào hợp lý nhất và thuê người giúp việc ngày Tết ở đâu uy tín tại Hà Nội? Theo dõi bài viết dưới đây, khách hàng có thể giải quyết triệt để vấn đề thuê giúp việc ngày Tết vô cùng nan giải này nhé!', 'content/images_dich_vu/5fc108dd7e99e_icon-giupviectet-1.png'),
(12, 'Hót cứt trẻ em', 'Dành riêng cho Hoàng Quốc Bảo Việt (developer)', 'content/images_dich_vu/5fc127b3043b3_ánhhit.jpg');

